const mongoose = require('mongoose')


const Schema = mongoose.Schema;

const peliSchema = new Schema({
    titulo:String,
    fecha: Date,
    sinopsis: String,
    caratula:String,
    directores: {
        type:[{name:String}]
    }
},{timestamps:true});

const Peliculas = mongoose.model('Peliculas',peliSchema)
mongoose.export = {Peliculas}
